import {Component, ElementRef, Inject, OnInit, ViewChild} from '@angular/core';
// import * as jsPDF from 'jspdf';
import {DOCUMENT} from "@angular/common";
import {PdfService} from "../../services/pdf.service";
import {SharedService} from "../../services/shared.service";


declare var $: any;

@Component({
  selector: 'app-licence',
  templateUrl: './barcode.component.html',
  styleUrls: ['./barcode.component.scss']
})
export class BarcodeComponent implements OnInit {
  @ViewChild('toPDF') pdf: ElementRef;
  @ViewChild('barcode') bc: ElementRef;
  doc;
  barCode;
  licenceNo;
  licence: String = 'data:image/png;base64,';


  planLogo: String = 'data:image/png;base64,';
  pLogo: String = 'data:image/png;base64,';

  constructor(@Inject(DOCUMENT) private document: Document, private pdfServ: PdfService, private sharedService: SharedService) {
  }

  ngOnInit() {
    const data = this.sharedService.getUserData();
    this.licenceNo = this.sharedService.extractData(data, "GQE_OTC_CARD_NO").value;
  }

  svgString2Image(svgString, width, height, format, callback) {
    format = format ? format : 'png';
    let svgData = 'data:image/svg+xml;base64,' + svgString;
    let canvas = this.document.createElement('canvas');
    let context = canvas.getContext('2d');
    canvas.width = width;
    canvas.height = height;
    let image = this.bc.nativeElement.querySelector('#dummyImage');
    image.crossOrigin = '';
    image.onload = function () {
      context.clearRect(0, 0, width, height);
      context.drawImage(image, 0, 0, width, height);
      let pngData = canvas.toDataURL();
      callback(pngData);
    };
    image.src = svgData;
  }

  createBarcode(type) {
    this.bc.nativeElement.querySelector('svg').removeAttribute("xmlns");
    const code = this.bc.nativeElement.querySelector('svg');
    this.barCode = new XMLSerializer().serializeToString(code);
    const encodedData = (<any>window).btoa(this.barCode);
    const dataURL: any = this.svgString2Image(encodedData, 800, 600, 'png',
      (blob) => {
        (type === 'download') ? this.pdfServ.createBarCode(blob) : this.pdfServ.printBarcode(blob);
      })
  }

  download() {
    this.createBarcode('download');
  }

  print() {
    this.createBarcode('print');
  }

}
